import React, { useState } from 'react';
import useStyles from './styles';
import { TextField, Button, Typography, Paper, Container } from '@material-ui/core';
import FileBase from 'react-file-base64';
import { useDispatch } from 'react-redux';
import { createPost } from '../ ../actions/posts';

const Form =()=>{

    const [postData, setPostData]=useState({creator:"",title:"",message:"",tags:"",selectedFile:""})

    const classes=useStyles();
    const dispatch=useDispatch();
    const clear = () => {

    };
    const handleSubmit=(e)=>{
        e.preventDefault();

        dispatch(createPost(postData));


    }
    return(
        <Paper className={classes.paper}>
            <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
                <Typography variant='h6'>Creating a Memory</Typography>
                <TextField name='creator'variant='outlined'label='Creator'fullWidth value={postData.creator}onChange={(e)=>setPostData({...postData, creator: e.target.value})}/>
                <TextField name='title'variant='outlined'label='Title'fullWidth value={postData.creator}onChange={(e)=>setPostData({...postData, creator: e.target.value})}/>
                <TextField name='message'variant='outlined'label='Message'fullWidth value={postData.creator}onChange={(e)=>setPostData({...postData, creator: e.target.value})}/>
                <TextField name='tags'variant='outlined'label='Tags'fullWidth value={postData.creator}onChange={(e)=>setPostData({...postData, creator: e.target.value})}/>
                <FileBase type="file"multiple={false}onDone={({base64})=>setPostData({...postData,selectedFile:base64})}/>
                <Button className={classes.buttonSubmit} variant="contained" color='primary' size='large' type="submit" fullWidth >Submit</Button>
                <Button  variant="contained" color='secondary' size='small' onClick={clear} fullWidth >Clear</Button>

            </form>
        </Paper>
    );
}
export default Form;
